mapper.select:
SELECT

FROM report_mon_overproof a
WHERE a.id = #{id}

SELECT 

FROM report_mon_overproof a WHERE a.del_flag = #{DEL_FLAG_NORMAL}
	and a.monthReportId = #{monthReportId}
	and a.type = #{type}

mapper.update:
